html:
{# include 'barcode_batch/print_format/pr_barcode_batch/pr_barcode_batch.html' #}
{%- for row in doc.items -%}
{%- for qty in range(row.qty|int) -%}
<div style="height=1in; width:2in; text-align:center">
<br>
{{ row.item_code }}
<br>
<svg class="barcode"
				jsbarcode-margin="0"
				jsbarcode-margintop="0"
				jsbarcode-marginbottom="1"
				jsbarcode-height="25"
				jsbarcode-width="1"
				jsbarcode-fontsize="12"
				jsbarcode-flat="true"
				jsbarcode-value="{{ row.batch_no or row.item_code}}{{ "|" + row.quality_inspection if row.quality_inspection }}"/></br>
<br><br>
</div>
{%- endfor -%}
{%- endfor -%}

<script src="https://cdn.jsdelivr.net/npm/jsbarcode@3.9.0/dist/JsBarcode.all.min.js"></script>
<script>
JsBarcode(".barcode").init();
</script>

<style>
	@media screen {
		.print-format {
			width: 2in;
			padding: 0;
			min-height: 1in;
		}
	}
</style>
